button.script:
function (stack) {
    var left;
    return {
        execute: function () {
            left = stack.pop();
            stack.push(Math.sqrt(left));
        },
        undo: function () {
            stack.pop();
            stack.push(left);
        }
    };
}

function (stack) {
    var left;
    return {
        execute: function () {
            left = stack.pop();
            stack.push(Math.log(left));
        },
        undo: function () {
            stack.pop();
            stack.push(left);
        }
    };
}

function (stack) {
    var left, right;
    return {
        execute: function () {
            left = stack.pop();
            right = stack.pop();
            stack.push(Math.pow(right, left));
        },
        undo: function () {
            stack.pop();
            stack.push(right);
            stack.push(left);
        }
    };
}

function (stack) {
    var left;
    return {
        execute: function () {
            left = stack.peek();
            stack.push(left);
        },
        undo: function () {
            stack.pop();
        }
    };
}

function (stack) {
    var left, right;
    return {
        execute: function () {
            left = stack.pop();
            right = stack.pop();
            stack.push(left);
            stack.push(right);
        },
        undo: function () {
            left = stack.pop();
            right = stack.pop();
            stack.push(left);
            stack.push(right);
        }
    };
}

